helper = ''


def check(str):
	i = len(str)//2
	for j in range(1, i+1):
		if (str[-j:] == str[-j*2:-j]):
			return False
	return True




def gen(str):
	valid = check(str)
	if not valid:
		return ''
	if (len(str) == 5000 and valid):
		return str
	for c in 'NOP':
		new_str = gen(str + c)
		if new_str:
			return new_str
	return ''


def main():
	while True:
		n = int(input())
		if n == 0:
			break
		print(helper[:n])

main()
